	var _date = new Date(g_form.getValue('u_when_needed'));
	//var _date_formatted = new Date(Date.parse(g_form.getValue('u_when_needed'))).format("MM/dd/yyyy");

	var _month = (_date.getMonth() + 1).toString();
	_month = (_month.length < 2) ? '0' + _month : _month;

	var _day = _date.getDate().toString();
	_day = (_day.length < 2) ? ('0' + _day) : _day;

	var _date_formatted = _month + '/' + _day + '/' + _date.getFullYear();

	//var _hours  = (_date.getHours() > 12) ? (_date.getHours() - 12) : _date.getHours();
	var _hours    = _date.getHours();
	var _meridian = 'AM';
	if(_date.getHours() > 12) {
		_hours    = _date.getHours() - 12;
		_meridian = 'PM';
	}
	var _mins   = (_date.getMinutes().toString().length < 2) ? ('0' + _date.getMinutes()) : _date.getMinutes();
	var _secs   = (_date.getSeconds().toString().length < 2) ? ('0' + _date.getSeconds()) : _date.getSeconds();
	var _time_formatted = _hours + ':' + _mins + ':' + _secs + ' ' + _meridian;

	//console.log(_log_filter + 'date formatted: ' + _date_formatted);

	//console.log(_log_filter + 'When: ' + g_form.getValue('u_when_needed'));
	console.log(_log_filter + g_form.getValue('number') + ': ' + g_form.getValue('short_description'));
	console.log(_log_filter + 'When: ' + g_form.getValue('u_when_needed') + ' (' + _date_formatted + ' before ' + _time_formatted + ') - What: ' + g_form.getValue('u_what_needed'));

	//g_form.addInfoMessage('All Done Loading!!');
	//g_form.addErrorMessage('Oops, there was an error!!');


	logIt(g_form.getSections());
	logIt(g_form.getSectionNames());

	var _sections = g_form.getSections();
