_ajax.getXMLAnswer(callback);

	console.log('[SAR] begin...');

function callback(pAnswer) {
	console.log('[SAR] in callback');
	//var _data = pAnswer + ' ' + this.getParameter('sysparm_userid');

	g_form.setValue('u_requested_for', g_user.userID);
	g_form.setValue('description', pAnswer);

	console.log('[SAR] data: ' + pAnswer);
}


