sys_script_client.script:
function logIt(pValue) {
	var _log_filter = '[sar] ';
	//console.log(_log_filter + pValue);
	jslog(_log_filter + pValue);
}

function onLoad() {
	if(g_form.isNewRecord()) {
		//g_form.setValue('u_requested_for', g_user.getFullName()); //this seems to work too
		g_form.setValue('u_requested_for', g_user.userID);
		logIt('Set current user as default value for "Requested For" field (' + g_user.userID + ')  (' + g_user.getFullName() + ')');
	}
	/*
