sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

    type: 'NeedItUtils',

	isDatePast: function(pDate) {
		var _now  = new GlideDateTime();
		var _date = new GlideDateTime(pDate);

		//gs.addInfoMessage('isDatePast: ' + _now + ' - ' + _date);

		//if(_date.before(_now)) {
		if(_date < _now) {
			//gs.addInfoMessage("Date is in the past!");
			return true;
		}
		//gs.addInfoMessage("Date is NOT in the past!");
		return false;
	},

	isDateToday: function(pDate){
		//var _now  = new GlideDate();
		//var _date = new GlideDate(); //GlideDate adds 7 hours to pDate??
		//_date.setValue(pDate);
		var _now  = new GlideDateTime();
		var _date = new GlideDateTime(pDate);
		_now      = _now.getLocalDate();
		_date     = _date.getLocalDate();

		//gs.addInfoMessage('isDateToday: ' + _now + ' - ' + _date);

		if(_date == _now) {
			//gs.addInfoMessage("Date is Today!!");
			return true;
		}
		//gs.addInfoMessage("Date is NOT Today!");
		return false;
	}

};
