sys_script_include.script:
//REMEMBER - Call these static methods from Script Includes(server-side) and not Client Scripts(client-side)
var SarTool = Class.create(); 

SarTool.getMeridiamFromTime = function(pTime){
	var _time = String(pTime).split(' ')[1];
	var _parts = _time.split(':');
	var _ampm  = 'AM';

	if(_parts[0] > 12) {
		_parts[0] -= 12;
		_ampm = 'PM';
	}

	return _parts[0] + ':' + _parts[1] + ':' + _parts[2] + ' ' + _ampm;
};

SarTool.isValidEmailAddress = function(pValue) {  
	if(String(pValue).match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
		return true;
	}
	return false;
};
