sys_script_include.script:
var DbDemo = Class.create();
DbDemo.prototype = { initialize: function() {},
    type: 'DbDemo',

	displayUser: function(pSysUserId){
		var _user = new GlideRecord("sys_user");
		_user.get(pSysUserId);
		gs.addInfoMessage(_user.sys_id + ' | ' + _user.name + ' | ' + _user.email + ' | ' + _user.user_name + ' | ' + _user.title + ' | ' + _user.department.name);
	},

	displayNeedItRequests: function() {
		var _needit = new GlideRecord('x_58872_needit_needit');
		_needit.query();
		gs.addInfoMessage('There are ' + _needit.getRowCount() + ' Need It requests:');

		while(_needit.next()) {
			gs.addInfoMessage(_needit.number + ': ' + 'need by: ' + _needit.u_when_needed + ' | req by: ' + _needit.u_requested_for + ' | req type: ' + _needit.u_request_type + ' | what: ' + _needit.u_what_needed + ' (' +_needit.short_description + ')');

		}

	}

};
